const findHotelButton=document.querySelector(".searching-hotel-button"),popupHotel=document.querySelector(".modal"),form=popupHotel.querySelector(".searching-form"),dateArrival=popupHotel.querySelector(".modal-date-arrival"),dateDeparture=popupHotel.querySelector(".modal-date-departure"),adultNumber=popupHotel.querySelector(".adult-number"),childrenNumber=popupHotel.querySelector(".children-number");findHotelButton.addEventListener("click",function(){popupHotel.classList.toggle("modal-show"),popupHotel.classList.remove("modal-error")}),form.addEventListener("submit",function(e){dateArrival.value&&dateDeparture.value&&(adultNumber.value||childrenNumber.value)||popupHotel.classList.add("modal-error"),e.preventDefault()});